INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (20, 'EVOPLAY', 'evoplay', '<div><p>EvoPlay — большая международная компания, ведущий разработчик комплексных решений и продуктов для игровой онлайн-индустрии. Наш бренд появился на рынке более 10 лет назад и с тех пор активно развивается.</p>
