INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (27, 'PROMO', 'promo', '<div><p>PROMO — первое украинское агентство Performance-маркетинга, которому доверяют гиганты e-commerce: Coca-Cola, Київстар, KFC, lifecell, Ельдорадо, ALLO, JYSK, Фокстрот и др. <br/>Компания на рынке с августа 2014, за это время мы:<br/>— вошли в рейтинг ТОП агентств Google Partners и получили статус Premier-партнера;<br/>— стали партнером «Коммуникационной группы № 1 в Украине» — Dentsu Aegis — в нише Performance Marketing;<br/>— выросли с 20 специалистов в 95+ крутых экспертов;<br/>— перешли на Agile-методологию и стали первыми в своей сфере, кто имплементировал Скрам (делимся опытом <a href="http://goo.gl/BbX4la);" target="_blank">goo.gl/BbX4la);</a><br/>— провели более 300 внутренних семинаров;<br/>— наполнили портфолио брендами, которые мы все любим (смотри на сайте).</p>
<p>Основные направления деятельности PROMO:<br/>— SEO;<br/>— Контекстная реклама;<br/>— UX / UI;<br/>— Magento разработка.</p>
<p>PROMOвци являются сертифицированными специалистами Google Adwords, Google Analytics, Яндекс.Директ, а агентство — партнером Googlе, а также просто Поисковым Гуру.</p></div>', null, 'static/images/brand/promo.png', 'http://promo.ua', null, 0, 0, 0, '2017-04-15 13:07:37.262026', 'Киев', null, null);
INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (28, 'SMART business', 'smart business', '<div><p>Компанія SMART business надає консультаційні послуги з вибору, розробки, навчання і супроводження ERP та CRM систем, а також послуги в сфері впровадження систем управління підприємством лінійки Microsoft Dynamics (Microsoft Dynamics AX, Microsoft Dynamics NAV, Microsoft Dynamics CRM) для підприємств різних форм власності, розмірів та галузі.</p>
