INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (29, 'EPAM', 'epam', '<div><p style="font-family: ''Trebuchet MS'', sans-serif!important; background-color: #39C2D7; font-size: 20px; color: #ffffff; padding: 20px 40px; text-align: center;">Engineering is in our DNA. Since 1993, we’ve helped the world’s leading companies imagine, design, engineer, and deliver software that changes the world. Today, we are more than developers. <br/> <br/>Our offices are digital laboratories. Our clients are major global brands. And we’re always looking for talented teammates. </p>
