INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (32, 'ENAVATE', 'enavate', '<div><h4>Our Business</h4>
