INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (38, 'BETLAB', 'betlab', '<div><p>BETLAB is Ukrainian Software product company for Sportsbook sphere.<br/>We started at 2014 in Kiev.</p>
