INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (39, 'PulsarFour', 'pulsarfour', '<div><p>We design, build and support enterprise e-commerce solutions for the US and European retail brands. We are a Digital Commerce Agency, highly specialized in our field of expertise — online retail technology, servicing clients from the delivery centers in Ukraine and Poland as well as front offices in San Francisco and Munich.</p>
