INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (44, 'Depositphotos', 'depositphotos', '<div><p>Depositphotos — успешный международный фотобанк с украинскими корнями. Мы предоставляем сервис покупателям и продавцам высококачественных стоковых фотографий, векторных изображений и видеороликов пользователям из 192 стран, обеспечивая их поддержкой на 20 языках. </p>
<p>Главный офис Depositphotos находится в Нью-Йорке, США. В 2010 году компания открыла офис разработки в Киеве и собрала команду специалистов для работы над новыми сервисами. Сегодня Depositphotos входит в пятерку крупнейших фотобанков мира, конкурируя с такими гигантами как Shutterstock и Getty Images.</p>
<p>Мы гордимся тем, что все специалисты компании работают на одной волне и понимают друг друга с полуслова. Нам нравится работать над продуктом, в который мы верим — и мы работаем. Много работаем.</p>
<p>Усилия Depositphotos заметны: в декабре 2015 года компания получила $5 млн. инвестиций от Европейского банка реконструкции и развития (ЕБРР) и TMT Investments. Значительную часть этих средств мы планируем вложить в развитие своей команды: действующих и новых партнеров компании — лучших украинских специалистов мирового уровня.</p></div>', null, 'static/images/brand/depositphotos.png', 'http://depositphotos.com/', null, 0, 0, 0, '2017-04-15 13:08:08.588052', 'Киев', null, null);
INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (45, 'Luxoft', 'luxoft', '<div><p>Luxoft provides high-end business solutions to clients across the globe. With deep domain expertise in the finance, telecom, energy, automotive, travel and aviation industries, the company consistently goes beyond its clients’ expectations by bringing together technology, talent, innovation, and the highest quality standards.</p>
<p><strong>Luxoft</strong> builds long-lasting partnerships with Boeing, IBM, Deutsche Bank, UBS, Harman, Avaya, Alstom, Sabre, Ford, Hotwire and many others by providing seasoned business and technology professionals who deliver high-quality solutions that fit the client’s needs.</p>
<p>We offer international delivery capabilities through our network of state-of-the-art delivery centers around the world. Luxoft`s customers benefit from the right mix of technology skills, industry knowledge, proprietary processes, methodologies, and a choice of engagement models.</p>
<p><strong>Today, Luxoft is +10000 collaborators strong and present on 5 Continents, 16 Countries with 30 Offices and 27 Delivery Centers. </strong></p>
<p><strong>The Luxoft Kiev office</strong> opened in spring of 2005 and since then has expanded to become one of the key hubs for European clients.</p>
<p><strong>Luxoft Dnepropetrovsk</strong> works with European as well as American clients seeking specialized software development resources, strong scalability and low attrition rates.</p>
<p><strong>Luxoft Odessa</strong> was established to serve a leading European automotive electronics client but the location also works with other near shore European as well as international clients seeking specialized and complex software development resources.</p>
<p><strong>Luxoft mission</strong> is to provide our clients with high-end IT services &amp; solutions delivered by top talents.</p>
<p>We are looking for talents with a passion for technology &amp; ready to create original solutions.</p>
<p>Once onboard, you are invited to expand your knowledge &amp; skills, offering you a continuous learning experience helping you stretch your potential.</p>
<p><strong>So if you’re enthusiastic by the idea of accessing cutting edge technology &amp; innovation to make an impact, why don’t you join us?</strong></p></div>', null, 'static/images/brand/luxoft.png', 'http://www.luxoft.com/', null, 0, 0, 0, '2017-04-15 13:08:10.351967', 'Киев, Днепр, Одесса', null, null);
