INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (50, 'Playtech', 'playtech', '<div><p>Playtech is the world’s largest online gaming software supplier traded on the London Stock Exchange Main Market, offering cutting-edge, value-added solutions to the industry’s leading operators. Company’s business portfolio consists of the most prominent names in the business, including William Hill, Netplay, Betfred, Mansion, Bet365, etc.</p>
