INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (75, 'ЛУН', 'лун', '<div><p>ЛУН — продуктовая IT-компания, развивающая интернет проекты по недвижимости в Украине и за рубежом.<br/>За 8 лет мы стали настоящими экспертами в теме поиска жилья, заняли лидирующую позицию в Украине, запустили проекты по всему миру (33 страны).</p>
