INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (82, 'Abweb', 'abweb', '<div><p>Кто мы такие? <br/>Мы — современная и надёжная команда единомышленников, с новыми и свежими взглядами на все бизнес процессы клиентов. На сегодняшний день предлагаем целый комплекс услуг, нацеленных на привлечение клиентов для вашего бизнеса. В работе ориентируемся в первую очередь на результат и выгоды, которые вы можете получить при сотрудничестве с нами.</p>
