INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (83, '2GIS', '2gis', '<div><p>2GIS — это международная компания, представлена на 4 континентах и более чем в 250 городах мира с уютным офисом в Киеве.<br/>Работаем с драйвом стартапа, но с гарантиями большой и стабильной компании :)</p>
