INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (106, 'Си Пи Ай - Киев, ТОВ', 'си пи ай   киев, тов', '<div><p>Компания «Си Пи Ай — Киев», является инженерным подразделением мирового лидера в производстве купюроприемников, монетоприемников, а также торговых автоматов (<a href="http://www.cranepi.com" target="_blank">www.cranepi.com</a>).</p>
