INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (132, 'IT4Medicine', 'it4medicine', '<div><p>IT4Medicine — проект, що сприяє розвитку сфери електронної системи охорони здоров’я в Україні.</p>
