INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (133, 'Talkable', 'talkable', '<div><p>Talkable is referral platform adds incentivized sharing to your sales funnel to create a powerful, new acquisition channel. <br/>Talkable is a flexible software platform that lets you: <br/>get your customers to refer their friends, reward them when their friends buy, use analytics and A/B testing to improve results.</p>
