INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (148, 'Form.com', 'form.com', '<div><p><a href="http://Form.com" target="_blank">Form.com</a> is a global leader in data collection and business process optimization software. </p>
