INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (149, '4Sync Inc.', '4sync inc.', '<div><p>4Sync Inc. — международная IT компания, которая специализируется на разработке облачных технологий хранения данных. С момента своего основания в 2011 году, 4Sync занимается разработкой и совершенствованием собственного уникального продукта — облачного сервиса для пользователей и сопутствующих мобильных/десктоп приложений.</p>
<p>В компании работают настоящие профессионалы своего дела. Всех объединяет желание работать над постоянным развитием продукта. Всегда находимся в поиске новых идей, совершенствуем качество нашего продукта и внедряем передовые технологии. </p>
<p>Хотите присоединиться к нашей команде? <br/>В 4Sync Вам непременно понравится:<br/>— Достойная заработная плата, приятные бонусы<br/>— Медицинское страхование, 100% оплачиваемый больничный<br/>— 75% оплаты курсов и тренингов, чтобы повысить профессиональную квалификацию, получить новые знания и практический опыт<br/>— <nobr>24-дневный</nobr> оплачиваемый отпуск<br/>— Возможность работать над масштабными, интересными проектами в сфере передовых облачных технологий<br/>— Демократический стиль управления с пониманием потребностей и интересов каждого <br/>— Участие в международных IT конференциях<br/>— Работа в дружной команде<br/>— Веселые и назабываемые корпоративные мероприятия</p>
<p>Работа в 4Sync вдохновляет!</p></div>', null, 'static/images/brand/4sync-inc.png', 'http://4sync.com/', null, 0, 0, 0, '2017-04-15 13:11:11.007602', 'Киев', null, null);
INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (150, 'Redwerk', 'redwerk', '<div><p><iframe allowfullscreen="" frameborder="0" height="315" src="https://www.youtube.com/embed/g5b_-2-DQ5E" width="560"></iframe></p>
