INSERT INTO parseville_company (id, name, alias, description, short_text, logo, url, event_url, show, show_on_main, has_parser, date, extra, news_url, vacancy_url) VALUES (154, 'Devellar', 'devellar', '<div><p>Devellar is the team of professionals united by a passion for the web development and eCommerce.</p>
